"$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
"type": "AdaptiveCard",
"version": "1.0",
"body": [
    {
    "type": "TextBlock",
    "text": "@{body('Alert_-_Get_incident')?['properties']?['title']}",
    "size": "large",
    "weight": "bolder",
    }, 
    {
    "type": "TextBlock",
    "text": "Assyst Ticket: @{outputs('Ticket_Number_eventRef')}",
    "size": "large",
    "weight": "bolder",
    }, 
    {
    "type": "TextBlock",
    "text": "Alert ID: @{body('Alert_-_Get_incident')?['properties']?['incidentNumber']}",
    "size": "large",
    "weight": "bolder",
    }, 
    {
    "type": "TextBlock",
    "text":  "Severity: @{body('Alert_-_Get_incident')?['properties']?['severity']}",
     "size": "large",
    "weight": "bolder",
    }, 
    {
      "type": "TextBlock",
      "text": "Cause of  Alert:",
    },
{
    "type": "Input.ChoiceSet",
    "id":  "Classification",
    "style":  "compact",
    "isMultiSelect": false,
    "value": "1",
    "choices": [
    {
          "title": "Unknown",
          "value": "Unknown"
    },
    {
          "title": "N/A",
          "value": "N/A"
    },
    {
          "title": "Hardware / Software Failure",
          "value": "Hardware / Software Failure"
    },
    {
          "title": "User Error",
          "value": "User Error"
    },
    {
          "title": "Expected Behaviour",
          "value": "Expected Behaviour"
    },
    {
          "title": "Rule Missfire",
          "value": "Rule Missfire"
    },
    {
          "title": "Scan",
          "value": "Scan"
    },
    {
          "title": "Misconfiguration",
          "value": "Misconfiguration"
    },
    {
          "title": "Ransomware",
          "value": "Ransomware"
    },
    {
          "title": "Phishing",
          "value": "Phishing"
    },
    {
          "title": "Adware / PUP",
          "value": "Adware / PUP"
    },
    {
          "title": "Exfiltration",
          "value": "Exfiltration"
    },
    {
          "title": "Data Leak",
          "value": "Data Leak"
    },
    {
          "title": "Key Logger",
          "value": "Key Logger"
    },
    {
          "title": "DDoS",
          "value": "DDoS"
    },
  {
          "title": "C2",
          "value": "C2"
    },    {
          "title": "Privilege Escalation",
          "value": "Privilege Escalation"
    },
    {
          "title": "Rogue AP",
          "value": "Rogue AP"
    }
]
},
    {
      "type": "TextBlock",
      "text": "Additional Information"
    },
    {
    "type": "Input.Text",
    "id":  "Additional Info",
    "placeholder":  "Additional Information",
    "maxLength": 2000,
    "isMultiline": true
    }
],
"actions": [
    {
    "type": "Action.Submit",
    "title": "True Positive",
    },
    {
    "type": "Action.Submit",
    "title": "False Positive",
    },
  {
    "type": "Action.Submit",
    "title": "Benign Positive",
    },
  {
    "type": "Action.Submit",
    "title": "Escalate",
    },
  {
    "type": "Action.Submit",
    "title": "Assign to another department",
